import {
  Component,
  OnInit,
  OnDestroy,
  ViewChild,
  ElementRef,
  Input,
} from '@angular/core';
import { MediaService } from 'src/app/Services/player-control/media.service';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-player-controls',
  templateUrl: './player-controls.component.html',
  styleUrls: ['./player-controls.component.css'],
})
export class PlayerControlsComponent implements OnInit, OnDestroy {
  @ViewChild('audioPlayer', { static: true })
  audioPlayer!: ElementRef<HTMLAudioElement>;

  @Input() selectedSong: any;
  @Input() songs: any[] = [];

  currentSongSubscription: Subscription;
  playIcon: string = 'fa-play';
  isPlaying: boolean = false;
  progressInterval: any;
  currentAudioElement: HTMLAudioElement | null = null;

  constructor(private mediaService: MediaService) { }

  ngOnInit() {
    this.setupAudioEvents();

    this.currentSongSubscription = this.mediaService.currentSong$.subscribe(
      (song) => {
        if (song) {
          this.selectSong(song);
        }
      }
    );
  }

  ngOnDestroy() {
    this.pause(); // Pausar la canción cuando el componente se destruya
    this.audioPlayer.nativeElement.removeEventListener(
      'ended',
      this.handleEnded.bind(this)
    );
    this.audioPlayer.nativeElement.removeEventListener(
      'timeupdate',
      this.handleTimeUpdate.bind(this)
    );
    clearInterval(this.progressInterval);
  }

  setupAudioEvents() {
    this.audioPlayer.nativeElement.addEventListener('ended', this.handleEnded.bind(this));
    this.audioPlayer.nativeElement.addEventListener('timeupdate', this.handleTimeUpdate.bind(this));
  }

  handleEnded() {
    console.log('Canción actual terminada, reproduciendo la siguiente...');
    this.play_next_song();
  }

loadAudio(audioUrl: string) {
  this.pause(); // Pausar la canción actual

  console.log('Cargando audio:', audioUrl);

  // Crear un nuevo elemento <audio>
  const newAudioElement = new Audio(audioUrl);
  newAudioElement.addEventListener('loadedmetadata', this.onLoadedMetadata.bind(this));
  newAudioElement.addEventListener('ended', this.handleEnded.bind(this));
  newAudioElement.addEventListener('timeupdate', this.handleTimeUpdate.bind(this));

  // Reemplazar el elemento <audio> actual por el nuevo
  this.currentAudioElement = newAudioElement;
}

  onLoadedMetadata() {
    console.log('Canción cargada completamente');
    this.play(); // Inicia la reproducción después de que la canción se haya cargado
  }
  pause() {
    if (this.currentAudioElement) {
      this.currentAudioElement.pause();
      this.updatePlayIcon(); // Actualizar el ícono de reproducción/pausa
      this.isPlaying = false;
      clearInterval(this.progressInterval);
    }
  }
  play() {
    if (this.currentAudioElement) {
      this.currentAudioElement.play()
        .then(() => {
          this.updatePlayIcon(); // Actualizar el ícono de reproducción/pausa
          this.isPlaying = true;
        })
        .catch((error) => {
          console.error('Error al reproducir el audio:', error);
        });
    }
  }
  
  togglePlayPause() {
    if (this.audioPlayer && this.audioPlayer.nativeElement) {
      if (this.audioPlayer.nativeElement.paused) {
        this.play(); // Reproducir la canción si estaba pausada
      } else {
        this.pause(); // Pausar la canción si se estaba reproduciendo
      }
    }
  }
  selectSong(song: any) {
    // Detener completamente la reproducción de la canción anterior
    this.stopAndResetAudio();

    this.selectedSong = song;
    this.loadAudio(song.audio);

    // Cargar los metadatos de la nueva canción
    if (this.audioPlayer && this.audioPlayer.nativeElement) {
      this.audioPlayer.nativeElement.load();
    }

    // Reproducir la nueva canción después de cargarla
    this.play();
  }

  stopAndResetAudio() {
    if (this.audioPlayer && this.audioPlayer.nativeElement) {
      this.audioPlayer.nativeElement.pause();
      this.audioPlayer.nativeElement.currentTime = 0;
      this.audioPlayer.nativeElement.src = ''; // Limpiar la fuente de audio
    }
  }
  play_next_song() {
    if (this.songs && this.songs.length > 0 && this.selectedSong) {
      this.pause(); // Pausar la canción actual
      const currentIndex = this.songs.findIndex(
        (song) => song.id === this.selectedSong.id
      );
      const nextIndex = (currentIndex + 1) % this.songs.length;
      const nextSong = this.songs[nextIndex];
      if (nextSong) {
        console.log('Siguiente canción:', nextSong);
        this.selectSong(nextSong);
      } else {
        console.log('¡La siguiente canción es indefinida!');
      }
    }
  }

  play_previous_song() {
    if (this.songs && this.songs.length > 0 && this.selectedSong) {
      this.pause(); // Pausar la canción actual
      const currentIndex = this.songs.findIndex(
        (song) => song.id === this.selectedSong.id
      );
      const previousIndex = (currentIndex - 1 + this.songs.length) % this.songs.length;
      const previousSong = this.songs[previousIndex];
      if (previousSong) {
        console.log('Canción anterior:', previousSong);
        this.selectSong(previousSong);
      } else {
        console.log('¡La canción anterior es indefinida!');
      }
    }
  }

  handleTimeUpdate(event: any) {
    if (this.audioPlayer && this.audioPlayer.nativeElement) {
      const progress = (this.audioPlayer.nativeElement.currentTime / this.audioPlayer.nativeElement.duration) * 100;
      const progressBar = document.getElementById('progressBar') as HTMLInputElement;
      if (progressBar) {
        progressBar.value = progress.toString();
      }
      this.updatePlayIcon(); // Actualizar el ícono de reproducción/pausa
      this.updateTimeDisplay(); // Actualizar la visualización de tiempo
    }
  }

  updatePlayIcon() {
    if (this.audioPlayer && this.audioPlayer.nativeElement) {
      const isPlaying = !this.audioPlayer.nativeElement.paused;
      const newPlayIcon = isPlaying ? 'fa-pause' : 'fa-play';
      this.playIcon = newPlayIcon;
      this.isPlaying = isPlaying;
    }
  }

  updateTimeDisplay() {
    if (this.audioPlayer && this.audioPlayer.nativeElement && this.audioPlayer.nativeElement.currentTime !== undefined && this.audioPlayer.nativeElement.duration !== undefined) {
      const timeLeft = document.querySelector('.time-left');
      const timeRight = document.querySelector('.time-right');
      if (timeLeft && timeRight) {
        timeLeft.textContent = this.formatTime(this.audioPlayer.nativeElement.currentTime);
        timeRight.textContent = this.formatTime(this.audioPlayer.nativeElement.duration);
      }
    }
  }

  handleProgressBar(event: any) {
    if (this.audioPlayer && this.audioPlayer.nativeElement) {
      const progress = event.target.value;
      const duration = this.audioPlayer.nativeElement.duration;

      if (!isNaN(duration) && isFinite(duration)) {
        const newTime = (duration * progress) / 100;
        if (!isNaN(newTime) && isFinite(newTime)) {
          this.audioPlayer.nativeElement.currentTime = newTime;
        }
      }
    }
  }


  // Eliminar este método
  startProgressInterval() {
    clearInterval(this.progressInterval);
    this.progressInterval = setInterval(() => {
      if (this.audioPlayer && this.audioPlayer.nativeElement) {
        const progress = (this.audioPlayer.nativeElement.currentTime / this.audioPlayer.nativeElement.duration) * 100;
        const progressBar = document.getElementById('progressBar') as HTMLInputElement;
        if (progressBar) {
          progressBar.value = progress.toString();
        }
      }
    }, 1000);
  }
  formatTime(seconds: number): string {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = Math.floor(seconds % 60);
    return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
  }
}
<!-- Audio player element -->
<audio #audioPlayer (timeupdate)="handleTimeUpdate($event)">
    Tu navegador no soporta el elemento de audio.
</audio>

<div class="container1" *ngIf="selectedSong">
    <!-- Media cover -->
    <div class="media-container">
        <div class="mediaDetails">
            <div>
                <img [src]="selectedSong?.album?.coverImage || selectedSong?.artist?.coverImage" alt="Media Cover" class="cover-image">
            </div>
        </div>
    </div>

    <!-- Progress bar -->
    <div class="progress-container">
        <div class="progress-style">
            <div class="time-left">{{ formatTime(audioPlayer?.nativeElement?.currentTime ? audioPlayer.nativeElement.currentTime : 0) }}</div>
            <input id="progressBar" type="range" class="form-range" min="0" max="100" [value]="(audioPlayer?.nativeElement?.currentTime / audioPlayer?.nativeElement?.duration * 100) || 0" (input)="handleProgressBar($event)">
            <div class="time-right">
                <p *ngIf="audioPlayer?.nativeElement?.duration; else elseBlock">
                    {{ formatTime(audioPlayer.nativeElement.duration) }}
                </p>
                <ng-template #elseBlock>
                    <p>0:00</p>
                </ng-template>
            </div>
        </div>
    </div>

    <!-- Song information -->
    <div class="song-info">
        <h3 class="title" *ngIf="selectedSong?.album?.artist?.artistName">
            {{ selectedSong?.songTitle }} - {{ selectedSong?.album?.artist?.artistName }}
        </h3>
        <h3 class="title" *ngIf="!selectedSong?.album?.artist?.artistName && selectedSong?.artist?.artistName">
            {{ selectedSong?.title }} - {{ selectedSong?.artist?.artistName }}
        </h3>
    </div>

    <!-- Player controls -->
    <div class="controls mt-3 player-button">
        <button (click)="play_previous_song()" class="btn btn-primary skip-btn">
            <i class="fas fa-backward"></i>
        </button>
        <button (click)="togglePlayPause()" class="btn btn-primary play-pause-btn">
            <i class="fas" [ngClass]="playIcon"></i>
        </button>
        <button (click)="play_next_song()" class="btn btn-primary skip-btn">
            <i class="fas fa-forward"></i>
        </button>
    </div>
</div>
