import { Component } from '@angular/core';
import { InformationService } from 'src/app/Services/Information/information.service';
import { EmailJSService } from '@emailjs/browser';

@Component({
  selector: 'app-information',
  templateUrl: './information.component.html',
  styleUrls: ['./information.component.css']
})
export class InformationComponent {

  formData = {
    nombre: '',
    email: '',
    mensaje: ''
  };
  ;

  constructor(private InformationService: InformationService, private emailJSService: EmailJSService) { }

  onSubmit() {
    this.emailJSService.sendForm('contact_form', 'YOUR_SERVICE_ID', this.formData)
      .then((response) => {
        console.log('Mensaje enviado con éxito!', response.status, response.text);
        // Realiza acciones adicionales después de enviar el formulario
      }, (error) => {
        console.log('Error al enviar el mensaje:', error);
        // Maneja el error según tus necesidades
      });
  }

}
