import { HttpErrorResponse } from '@angular/common/http';
import {Component} from '@angular/core';
import { Router } from '@angular/router';
import { UserService } from 'src/app/Services/user.service';

@Component({
  selector: 'app-authenticated',
  templateUrl: './authenticated.component.html',
  styleUrls: ['./authenticated.component.css']
})
export class AuthenticatedComponent{
  imagePath = '../../../assets/logo2.jpg';

  constructor(private uService:UserService,private route:Router){}

  logout(){
    this.uService.logoutUser().subscribe(
      (response) => {
        localStorage.removeItem('token');
        localStorage.removeItem('idUser');
        this.route.navigate(['login'])

      },
      (error) => {
        console.error('Error inesperado:', error);
      }
    )
  }

}

<nav class="navbar navbar-inverse" style="border-bottom: 1px solid white;">
  <div class="container-fluid">

    <div class="collapse navbar-collapse">

      <div class="navbar-header" style="width: 115px; height: 95px; padding-top: .5%; padding-bottom: .5%;" >
        <img style="width: 100%; height: 100%;" [src]="[imagePath]" alt="">
      </div>

      <ul class="nav navbar-nav navbar-right">
          <li class="dropdown perfil" appDropdown>
            <a href="#" class="dropdown-toggle" data-toggle="dropdown" role="button">Perfil <span class="caret"></span></a>
            <ul class="dropdown-menu" role="menu">
              <li><a [routerLink]="['editProfile/']" style="cursor: pointer;">Editar Perfil</a></li>
              <li><a [routerLink]="['editPodcast/','Crear']" style="cursor: pointer;">Crear Podcast</a></li>
              <li><a [routerLink]="['editPodcast/','Editar']" style="cursor: pointer;">Editar Podcast</a></li>
              <li class="divider"></li>
              <li><a (click)="logout()" style="cursor: pointer;">Cerrar sesión</a></li>
            </ul>
          </li>
      </ul>
    </div>
  </div>
</nav>
