import {
  Component,
  OnInit,
  OnDestroy,
  ViewChild,
  ElementRef,
  Input,
} from '@angular/core';
import { MediaService } from 'src/app/Services/player-control/media.service';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-player-controls',
  templateUrl: './player-controls.component.html',
  styleUrls: ['./player-controls.component.css'],
})
export class PlayerControlsComponent implements OnInit, OnDestroy {
  @ViewChild('audioPlayer', { static: true })
  audioPlayer!: ElementRef<HTMLAudioElement>;

  @Input() selectedSong: any;
  @Input() songs: any[] = [];

  currentSongSubscription: Subscription;
  playIcon: string = 'fa-play';
  audioContext: AudioContext | null = null;
  isPlaying: boolean = false;
  progressInterval: any;

  constructor(private mediaService: MediaService) {}

  ngOnInit() {
    this.setupAudioEvents();
    this.audioContext = new AudioContext();

    this.currentSongSubscription = this.mediaService.currentSong$.subscribe(
      (song) => {
        if (song) {
          this.selectedSong = song;
          this.loadAndPlayAudio(song.audio);
          this.printNextAndPreviousSongs();
        }
      }
    );

    this.mediaService.currentAlbum$.subscribe((album) => {
      if (album) {
        this.songs = album.songs;
        this.selectedSong = null; // Reiniciar la canción seleccionada
      }
    });
  }

  ngOnDestroy() {
    this.pause();
    this.audioPlayer.nativeElement.removeEventListener(
      'ended',
      this.handleEnded.bind(this)
    );
    this.audioPlayer.nativeElement.removeEventListener(
      'timeupdate',
      this.handleTimeUpdate.bind(this)
    );
    clearInterval(this.progressInterval);
  }

  setupAudioEvents() {
    this.audioPlayer.nativeElement.addEventListener(
      'ended',
      this.handleEnded.bind(this)
    );
    this.audioPlayer.nativeElement.addEventListener(
      'timeupdate',
      this.handleTimeUpdate.bind(this)
    );
  }

  handleEnded() {
    this.play_next_song();
  }

  loadAndPlayAudio(audioUrl: string) {
    this.pause();
    this.audioPlayer.nativeElement.src = audioUrl;
    this.audioPlayer.nativeElement.load();
    this.audioPlayer.nativeElement.play().then(() => {
      console.log('Reproduciendo...');
      this.playIcon = 'fa-pause';
      this.isPlaying = true;
      this.startProgressInterval();
    }).catch(error => {
      console.error('Error al reproducir el audio:', error);
    });
  }

  play() {
    if (this.audioPlayer && this.audioPlayer.nativeElement) {
      this.audioPlayer.nativeElement.play();
      console.log('Reproduciendo...');
      this.playIcon = 'fa-pause';
      this.isPlaying = true;
      this.startProgressInterval();
    }
  }

  pause() {
    if (this.audioPlayer && this.audioPlayer.nativeElement) {
      this.audioPlayer.nativeElement.pause();
      console.log('Pausado...');
      this.playIcon = 'fa-play';
      this.isPlaying = false;
      clearInterval(this.progressInterval);
    }
  }

  togglePlayPause() {
    if (this.audioPlayer && this.audioPlayer.nativeElement) {
      if (this.audioPlayer.nativeElement.paused) {
        this.play();
      } else {
        this.pause();
      }
    }
  }

  selectAlbum(album: any) {
    this.mediaService.setCurrentAlbum(album);
  }

  printNextAndPreviousSongs() {
    if (this.songs && this.songs.length > 0 && this.selectedSong) {
      const currentIndex = this.songs.findIndex(
        (song) => song.id === this.selectedSong.id
      );

      const previousIndex = (currentIndex - 1 + this.songs.length) % this.songs.length;
      const nextIndex = (currentIndex + 1) % this.songs.length;

      const previousSong = this.songs[previousIndex];
      const nextSong = this.songs[nextIndex];

      console.log('Canción actual:', this.selectedSong);
      console.log('Canción anterior:', previousSong);
      console.log('Siguiente canción:', nextSong);
    }
  }

  play_next_song() {
    if (this.songs && this.songs.length > 0 && this.selectedSong) {
      const currentIndex = this.songs.findIndex(
        (song) => song.id === this.selectedSong.id
      );
      const nextIndex = (currentIndex + 1) % this.songs.length;
      const nextSong = this.songs[nextIndex];
      if (nextSong) {
        this.selectedSong = nextSong;
        this.loadAndPlayAudio(nextSong.audio);
        this.printNextAndPreviousSongs();
      }
    }
  }

  play_previous_song() {
    if (this.songs && this.songs.length > 0 && this.selectedSong) {
      const currentIndex = this.songs.findIndex(
        (song) => song.id === this.selectedSong.id
      );
      const previousIndex = (currentIndex - 1 + this.songs.length) % this.songs.length;
      const previousSong = this.songs[previousIndex];
      if (previousSong) {
        this.selectedSong = previousSong;
        this.loadAndPlayAudio(previousSong.audio);
        this.printNextAndPreviousSongs();
      }
    }
  }

  handleTimeUpdate(event: any) {
    if (this.audioPlayer && this.audioPlayer.nativeElement) {
      const progress = (this.audioPlayer.nativeElement.currentTime / this.audioPlayer.nativeElement.duration) * 100;
      const progressBar = document.getElementById('progressBar') as HTMLInputElement;
      if (progressBar) {
        progressBar.value = progress.toString();
      }
    }
  }

  handleProgressBar(event: any) {
    if (this.audioPlayer && this.audioPlayer.nativeElement) {
      const progress = event.target.value;
      const duration = this.audioPlayer.nativeElement.duration;

      if (!isNaN(duration) && isFinite(duration)) {
        const newTime = (duration * progress) / 100;
        if (!isNaN(newTime) && isFinite(newTime)) {
          this.audioPlayer.nativeElement.currentTime = newTime;
        }
      }
    }
  }

  formatTime(seconds: number): string {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = Math.floor(seconds % 60);
    return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
  }

  startProgressInterval() {
    clearInterval(this.progressInterval);
    this.progressInterval = setInterval(() => {
      if (this.audioPlayer && this.audioPlayer.nativeElement) {
        const progress = (this.audioPlayer.nativeElement.currentTime / this.audioPlayer.nativeElement.duration) * 100;
        const progressBar = document.getElementById('progressBar') as HTMLInputElement;
        if (progressBar) {
          progressBar.value = progress.toString();
        }
      }
    }, 1000);
  }
}
