import { Component, OnDestroy, OnInit, ViewChild } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { Subscription } from 'rxjs';
import { UserService } from 'src/app/Services/user.service';

@Component({
  selector: 'app-change-password',
  templateUrl: './change-password.component.html',
  styleUrls: ['./change-password.component.css']
})
export class ChangePasswordComponent implements OnInit,OnDestroy {

  @ViewChild('myForm') myForm:NgForm;
  private token:string;
  urlSuscripcion:Subscription;
  resetSuscripcion:Subscription;
  redirect:boolean = false;
  mess:string = ''

  data = {
    "token": "",
    "password": ""
  }
  constructor(private route:ActivatedRoute,private uService:UserService) { }

  ngOnInit() {
    this.urlSuscripcion = this.route.queryParams.subscribe(
      params => {
        this.token = params['token'];
      }
    );
  }
  ngOnDestroy(){
    this.urlSuscripcion.unsubscribe();
    this.resetSuscripcion.unsubscribe();
  }

  onSubmit(){
    this.data.token = this.token;
    this.data.password = this.myForm.value.passwordConfirm;
    this.resetSuscripcion = this.uService.restablecerContraseña(this.data).subscribe(
      (response)=>{
        this.mess = response.message;
        this.redirect=true;

      },
      (error)=>{
        this.mess = error.error.error;
        this.redirect=false;
      }
    );

  }

}

  

<div class="container">
  <div class="card">
      <h3>Cambia tu contraseña</h3>
      <form #myForm="ngForm" (ngSubmit)="onSubmit()">
          <div class="form-group">
              <label for="passwordNew">Nueva Contraseña</label>
              <input type="password"
              id="passwordNew" minlength="8" ngModel name="passwordNew" placeholder="Nueva contraseña" required #passwordNew="ngModel">
              <span *ngIf="!passwordNew.valid && passwordNew.touched">La contraseña debe contener al menos 8 caracteres</span>
          </div>
          <div class="form-group">
              <label for="passwordConfirm">Confirmar nueva contraseña</label>
              <input type="password" id="passwordConfirm" ngModel name="passwordConfirm" placeholder="Confirmar contraseña"
                  required #passwordConfirm="ngModel">
              <span *ngIf="passwordNew.value !== passwordConfirm.value && passwordConfirm.touched">La contraseña no coincide</span>
              <span>{{mess}}</span>

          </div>
          <button type="submit" [disabled]="!myForm.valid || passwordNew.value !== passwordConfirm.value" class="btn-login btn btn-primary">Cambiar Contraseña</button>
          <button *ngIf="redirect" [routerLink]="['/login']" class="btn-redirect btn btn-primary">Regresar</button>
      </form>
      
  </div>
</div>
