import { Component, OnInit } from '@angular/core';
import { Artist } from './artist.model';
import { ArtistService } from 'src/app/Services/Artists/artists.service';
import { catchError, throwError } from 'rxjs';
import { ActivatedRoute, Router } from '@angular/router';
import { AlbumService } from 'src/app/Services/Album/album.service';

@Component({
  selector: 'app-artist',
  templateUrl: './artist.component.html',
  styleUrls: ['./artist.component.css']
})
export class ArtistComponent implements OnInit {


  listOfArtist: Artist[];
  artists: any[] = [];
  artistSelected: string = '';
  coverImages: any;
  currentArtistIndex: number;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private artistService: ArtistService,
    private albumService: AlbumService 
  ) { }


  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      const currentArtistIndexParam = params.get('currentArtistIndex');
      if (currentArtistIndexParam) {
        this.currentArtistIndex = parseInt(currentArtistIndexParam, 10);
      }
    });
  
    this.loadArtists();
  }
  loadArtists(): void {
    this.artistService.getArtists()
      .pipe(
        catchError(error => {
          console.error('Error fetching artists:', error);
          return throwError(error);
        })
      )
      .subscribe(artists => {
        this.artists = artists;
        if (this.currentArtistIndex >= 0 && this.currentArtistIndex < this.artists.length) {
          const artistId = this.artists[this.currentArtistIndex].id;
          this.artistSelected = this.artists[this.currentArtistIndex].artistName;
          this.albumService.getAlbumByartist(artistId);
        } else {
          console.warn('Invalid currentArtistIndex:', this.currentArtistIndex);
        }
      });
  }
  navigateToAlbum(artistId: number) {
    this.router.navigate(['/album', { currentArtistIndex: artistId }]);
  }


}
