import { Component, OnInit } from '@angular/core';
import { PodcastServiceService } from 'src/app/Services/Podcast/podcast-service.service';
import { ActivatedRoute } from '@angular/router';


@Component({
  selector: 'app-episodes',
  templateUrl: './episodes.component.html',
  styleUrls: ['./episodes.component.css']
})
export class EpisodesComponent implements OnInit {
  episodes: any;
  podcastId: number;

  constructor(
    private podcastService: PodcastServiceService,
    private route: ActivatedRoute) { }

  ngOnInit(): void {
    // Fetch the list of episodes by podcast
    this.route.paramMap.subscribe(params => {
      this.podcastId = +params.get('podcastId');
      if (this.podcastId) {
        this.podcastService
          .getEpisodesByPodcastId(this.podcastId)
          .subscribe(data => {
            this.episodes = data;
            console.log("datos extraídos", data);
          });
      }
    });
  }
}
<body>
    <div class="container-epi">
        <div class="episodes">
            <img [src]="episodes[0]?.podcast?.coverImage" alt="{{ episodes[0]?.podcast?.podcastTitle }}">
            <div class="episode-info">
                <h1>{{ episodes[0]?.podcast?.podcastTitle }}</h1>
                <h4>{{ episodes[0]?.podcast?.user?.username }}</h4>
                <p>{{ episodes[0]?.podcast?.description }}</p>
            </div>
        </div>

        <div class="episodes-all">
            <h5>Todos los episodios</h5>
            <hr class="line">
        </div>

        <!--List of episodes-->
        <div *ngFor="let episode of episodes" class="list-epi">
            <img [src]="episode.podcast?.coverImage" alt="{{ episodes[0]?.podcast?.podcastTitle }}">
            <div class="details">
                <h4>{{ episode?.episodeTitle }}</h4>
                <p>{{ episode?.description }}</p>
                <div class="inf-epi">
                    <button class="btn btn-primary play-pause-btn">
                        <i class="fas fa-play"></i>
                    </button>
                    <p>{{ episode?.releaseDate | date }}</p>
                    <p>{{ episode?.duration }}</p>
                </div>
            </div>
        </div>
    </div>
</body>
